const state = {
  cart: [],
  cartCount: 0,
};

const mutations = {
  getCart() {
    var cartArr = [];
    var cart = localStorage.getItem('cart');
    if(cart != null){
      cartArr = JSON.parse(cart).data;
    }
    return cartArr;
  },
  getCartCount() {
    return localStorage.getItem('cartCount');
  },
  addToCart(state, product) {
    var cartArr = [];
    var cartCount = 0;

    var cart = localStorage.getItem('cart');
    if(cart == null){
      cartArr.push(product);
      cartCount++;
    }
    else{
      cartArr = JSON.parse(cart).data;
      cartCount = localStorage.getItem('cartCount');

      if(!cartArr.some(item => item.id === product.id)){
        cartArr.push(product);
        cartCount++;
      }
    }

    var cartObj = {
      data:cartArr
    }

