import client from "../../api";


export default {
  namespaced: true,
  state: {
    isAuthenticated: false,
    userData: null
  },

  actions: {
    checkAuth(ctx) {
      return client.post("/api/account/auth")
        .then((response) => {
          ctx.commit('setUserData', response.data)
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeToken", response.data.token);

          return response
        })
    },
    login(ctx, payload) {
      payload = { email: "test@gmail.com", password: "1234" }
      return client.post("/api/account/signIn", payload)
        .then((response) => {
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeToken", response.data.data.userId);

          return response
        })
    }
  },

  mutations: {
    setUserData(state, userData) {
      state.userData = userData
    },

    setAuthentication(state, value) {
      state.isAuthenticated = value
    },

    logout(state) {
      state.isAuthenticated = false
      localStorage.removeItem("hungrybeeToken");
    }
  },

  getters: {
    isAuth(state) {
      return state.isAuthenticated
    },
    // getUser(state) {
    //   return state.userData
    // }
  },
}
