const state = {
  cart: [],
  cartCount: 0,
};

const mutations = {
  addToCart(state, product) {
    var cartArr = [];
    var cartCount = 0;

    var cart = localStorage.getItem('cart');
    if(cart == null){
      cartArr.push(product);
      cartCount++;
    }
    else{
      cartArr = JSON.parse(cart).data;
      cartCount = localStorage.getItem('cartCount');

      if(!cartArr.some(item => item.id === product.id)){
        cartArr.push(product);
        cartCount++;
      }
    }

    var cartObj = {
      data:cartArr
    }

    localStorage.setItem('cart', JSON.stringify(cartObj));
    localStorage.setItem('cartCount', JSON.stringify(cartCount));
  },
  removeProduct(state, product) {
    var cart = localStorage.getItem('cart');
    var cartObj = JSON.parse(cart);
    var cartCount = localStorage.getItem('cartCount');
    const existingProduct = cartObj.find(p => p.id === product.id);
    if (existingProduct) {
      cartObj.splice(cartObj.indexOf(existingProduct), 1);
      cartCount -= 1;

      localStorage.setItem('cart', JSON.stringify(cart));
      localStorage.setItem('cartCount', JSON.stringify(cartCount));
    }
  },
  clearCart(state) {
    state.cart = [];
    state.cartCount = 0;
    localStorage.removeItem('cart');
    localStorage.removeItem('cartCount');
  },
};

const getters = {
  cart: state => localStorage.getItem('cart'),
  cartCount: state => localStorage.getItem('cartCount'),
};

const actions = {
  initCart({ commit }) {
    const cart = JSON.parse(localStorage.getItem('cart'));
    const cartCount = JSON.parse(localStorage.getItem('cartCount'));
    if (cart) {
      commit('setCart', cart);
    }
    if (cartCount) {
      commit('setCartCount', cartCount);
    }
  },
};

export default {
  namespaced: true,
  state,
  mutations,
  getters,
  actions,
};
