const state = {
  cart: [],
  cartCount: 0,
};

const mutations = {
  addToCart(state, product) {
    var cart = localStorage.getItem('cart');
    var cartObj = JSON.parse(cart);
    var cartCount = localStorage.getItem('cartCount');
    if(!cartObj.some(item => item.id === product.id)){
      cartObj.push(product);
      cartCount++;
    }

    localStorage.setItem('cart', JSON.stringify(cart));
    localStorage.setItem('cartCount', JSON.stringify(cartCount));
  },
  removeProduct(state, product) {
    var cart = localStorage.getItem('cart');
    var cartObj = JSON.parse(cart);
    const existingProduct = state.cart.find(p => p.id === product.id);
    if (existingProduct) {
      if (existingProduct.quantity > 1) {
        existingProduct.quantity -= 1;
      } else {
        state.cart.splice(state.cart.indexOf(existingProduct), 1);
      }
      state.cartCount -= 1;
      localStorage.setItem('cart', JSON.stringify(state.cart));
      localStorage.setItem('cartCount', JSON.stringify(state.cartCount));
    }
  },
  clearCart(state) {
    state.cart = [];
    state.cartCount = 0;
    localStorage.removeItem('cart');
    localStorage.removeItem('cartCount');
  },
};

const getters = {
  cart: state => localStorage.getItem('cart'),
  cartCount: state => localStorage.getItem('cartCount'),
};

const actions = {
  initCart({ commit }) {
    const cart = JSON.parse(localStorage.getItem('cart'));
    const cartCount = JSON.parse(localStorage.getItem('cartCount'));
    if (cart) {
      commit('setCart', cart);
    }
    if (cartCount) {
      commit('setCartCount', cartCount);
    }
  },
};

export default {
  namespaced: true,
  state,
  mutations,
  getters,
  actions,
};
