import client from "../../api";


export default {
  namespaced: true,
  state: {
    isAuthenticated: false,
  },

  actions: {
    submitOrder(ctx, payload) {
      return client.post("/api/order/submit", payload)
        .then((response) => {
          ctx.commit('setUserData', response.data.data)
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeOrderId", response.data.orderId);

          return response
        })
    },
    trackOrder(ctx, orderId) {
      return client.post("/api/order/:orderId", orderId)
        .then((response) => {
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeToken", response.data.data.userId);

          return response
        })
    }
  },

  mutations: {
  },

  getters: {
  },
}
