export default {
  namespaced: true,
  state: {
    cartCount: 0,
    products:[],
  },

  actions: {
  },

  mutations: {
    addToCart(state, product){
      state.products.push(product);
      state.cartCount++;
    }

  },

  getters: {
    cartCount(state) {
      return state.cartCount
    },
    products(state) {
      return state.products
    }
  },
}
