import client from "../../api";


export default {
  namespaced: true,
  state: {
    isAuthenticated: false,
    userData: null
  },

  actions: {
    submitOrder(ctx, payload) {
      return client.post("/api/order/submit", payload)
        .then((response) => {
          ctx.commit('setUserData', response.data.data)
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeOrderId", response.data.orderId);

          return response
        })
    },
    trackOrder(ctx, orderId) {
      return client.post("/api/order/:orderId", orderId)
        .then((response) => {
          ctx.commit('setAuthentication', true)
          localStorage.setItem("hungrybeeToken", response.data.data.userId);

          return response
        })
    }
  },

  mutations: {
    setUserData(state, userData) {
      state.userData = userData
    },

    setAuthentication(state, value) {
      state.isAuthenticated = value
    },

    logout(state) {
      state.isAuthenticated = false
      localStorage.removeItem("hungrybeeToken");
    }
  },

  getters: {
    isAuth(state) {
      return state.isAuthenticated
    },
    getUser(state) {
      return state.userData
    }
  },
}
